INSERT INTO `yw_company_setting` VALUES('31','unique_skill','<strong><span style=\"text-indent: 28px;\">项目名称：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;智能手机叫车app后端系统</span><strong><span style=\"text-indent: 28px;\"><br />\r\n项</span><span style=\"text-indent: 28px;\">目</span><span style=\"text-indent: 28px;\">描</span><span style=\"text-indent: 28px;\">述：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"text-indent: 28px;\">主要对智能手机用户提供打车服务，主要功能有周围空车查询，排班点派车，距离最近找车，任务讯息转发，中心讯息订阅发布。用户查找周围的司机，建立他们之间的讯息沟通，产生并委派任务。司机执行任务，客户产生用户评价。<br />\r\n</span><strong><span style=\"text-indent: 28px;\">使用技术：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"text-indent: 28px;\">node.js, MySQL, Redis，使用RedisStore实现socket.io的多进程间讯息发送，增强了node.js的cluster能力。<br />\r\n<br />\r\n<br />\r\n<br />\r\n</span><strong><span style=\"text-indent: 28px;\">项目名称：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;出租车调度系统</span><strong><span style=\"text-indent: 28px;\"><br />\r\n项</span><span style=\"text-indent: 28px;\">目</span><span style=\"text-indent: 28px;\">描</span><span style=\"text-indent: 28px;\">述：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;本项目利用GPS，GPRS，GIS，呼叫中心等技术做车辆的及时派遣，追踪等信息处理。</span><span style=\"text-indent: 28px;\">&nbsp;</span><span style=\"text-indent: 28px;\">本人主要负责Server端的网络通信，业务逻辑实现。如主要的找车类型为，排班点找车，距离最近找车，5台抢标找车等。<br />\r\n</span><strong><span style=\"text-indent: 28px;\">使用技术：<br />\r\n</span></strong><span style=\"text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;c++</span><span style=\"text-indent: 28px;\">多线程并发处理，数据库，网络编程</span><span style=\"text-indent: 28px;\">，WinCE</span><span style=\"text-indent: 28px;\">。</span><br />');
