	$data['itemid']=$this->_itemid;		}//			if(!is_null($this->_fina_type)){//				 $data['fina_type']=$this->_fina_type;//			}		if(!is_null($this->_reason)){			$data['reason']=$this->_reason;		}		if(!is_null($this->_order_id)){			$data['order_id']=$this->_order_id;		}		if(!is_null($this->_uid)){			$data['uid']=$this->_uid;		}		if(!is_null($this->_username)){			$data['username']=$this->_username;		}		if(!is_null($this->_obj_type)){			$data['obj_type']=$this->_obj_type;		}		if(!is_null($this->_obj_id)){			$data['obj_id']=$this->_obj_id;		}		if(!is_null($this->_amount)){			$data['amount']=$this->_amount;		}		if(!is_null($this->_balance)){			$data['balance']=$this->_balance;		}//			if(!is_null($this->_fina_credit)){//				 $data['fina_credit']=$this->_fina_credit;//			}//			if(!is_null($this->_user_credit)){//				 $data['user_credit']=$this->_user_credit;//			}//			if(!is_null($this->_fina_source)){//				 $data['fina_source']=$this->_fina_source;//			}		if(!is_null($this->_addtime)){			$data['addtime']=$this->_addtime;		}//			if(!is_null($this->_recharge_cash)){//				 $data['recharge_cash']=$this->_recharge_cash;//			}		if(!is_null($this->_site_profit)){			$data['site_profit']=$this->_site_profit;		}		if(!is_null($this->_note)){			$data['note']=$this->_note;		}//			if(!is_null($this->_is_trust)){//				 $data['is_trust']=$this->_is_trust;//			}//			if(!is_null($this->_trust_type)){//				 $data['trust_type']=$this->_trust_type;//			}		if(!is_null($this->_bank)){			$data['bank']=$this->_bank;		}		if(!is_null($this->_editor)){			$data['editor']=$this->_editor;		}		if($this->_where){			return $this->_db->updatetable($this->_tablename,$data,$this->getWhere());		}		else{			$where = array('itemid' => $this->_itemid);			return $this->_db->updatetable($this->_tablename,$data,$where);		}	}	function query_keke_finance_record($is_cache=0, $cache_time=0){		if($this->_where){			$sql = "select * from $this->_tablename where ".$this->_where;		}		else{			$sql = "select * from $this->_tablename";		}		if ($is_cache) {			$this->_cache_config ['is_cache'] = $is_cache;		}		if ($cache_time) {			$this->_cache_config ['time'] = $cache_time;		}		if ($this->_cache_config ['is_cache']) {			if (CACHE_TYPE) {				$keke_cache = new keke_cache_class ( CACHE_TYPE );				$id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');				$data = $keke_cache->get ( $id );				if ($data) {					return $data;				} else {					$res = $this->_dbop->query ( $sql );					$keke_cache->set ( $id, $res,$this->_cache_config['time'] );					$this->_where = "";					return $res;				}			}		}else{			$this->_where = "";			return  $this->_dbop->query ( $sql );		}	}	function count_keke_finance_record(){		if($this->_where){			$sql = "select count(*) as count from $this->_tablename where ".$this->_where;		}		else{			$sql = "select count(*) as count from $this->_tablename";		}		$this->_where = "";		return $this->_dbop->getCount($sql);	}	function del_keke_finance_record(){		if($this->_where){			$sql = "delete from $this->_tablename where ".$this->_where;		}		else{			$sql = "delete from $this->_tablename where itemid = $this->_itemid ";		}		$this->_where = "";		return $this->_dbop->execute($sql);	}}?>