d;		}		if(!is_null($this->_username)){			$data['username']=$this->_username;		}		if(!is_null($this->_mark_max_time)){			$data['mark_max_time']=$this->_mark_max_time;		}		if(!is_null($this->_by_uid)){			$data['by_uid']=$this->_by_uid;		}		if(!is_null($this->_by_username)){			$data['by_username']=$this->_by_username;		}		if(!is_null($this->_aid)){			$data['aid']=$this->_aid;		}		if(!is_null($this->_aid_star)){			$data['aid_star']=$this->_aid_star;		}		if(!is_null($this->_mark_value)){			$data['mark_value']=$this->_mark_value;		}		if(!is_null($this->_mark_type)){			$data['mark_type']=$this->_mark_type;		}		if(!is_null($this->_mark_count)){			$data['mark_count']=$this->_mark_count;		}		if($this->_where){			return $this->_db->updatetable($this->_tablename,$data,$this->getWhere());		}		else{			$where = array('mark_id' => $this->_mark_id);			return $this->_db->updatetable($this->_tablename,$data,$where);		}	}	function query_keke_witkey_mark($is_cache=0, $cache_time=0){		if($this->_where){			$sql = "select * from $this->_tablename where ".$this->_where;		}		else{			$sql = "select * from $this->_tablename";		}		if ($is_cache) {			$this->_cache_config ['is_cache'] = $is_cache;		}		if ($cache_time) {			$this->_cache_config ['time'] = $cache_time;		}		if ($this->_cache_config ['is_cache']) {			if (CACHE_TYPE) {				$keke_cache = new keke_cache_class ( CACHE_TYPE );				$id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');				$data = $keke_cache->get ( $id );				if ($data) {					return $data;				} else {					$res = $this->_dbop->query ( $sql );					$keke_cache->set ( $id, $res,$this->_cache_config['time'] );					$this->_where = "";					return $res;				}			}		}else{			$this->_where = "";			return  $this->_dbop->query ( $sql );		}	}	function count_keke_witkey_mark(){		if($this->_where){			$sql = "select count(*) as count from $this->_tablename where ".$this->_where;		}		else{			$sql = "select count(*) as count from $this->_tablename";		}		$this->_where = "";		return $this->_dbop->getCount($sql);	}	function del_keke_witkey_mark(){		if($this->_where){			$sql = "delete from $this->_tablename where ".$this->_where;		}		else{			$sql = "delete from $this->_tablename where mark_id = $this->_mark_id ";		}		$this->_where = "";		return $this->_dbop->execute($sql);	}}?>