<?phpclass Keke_witkey_task_template_class{	public $_db;	public $_tablename;	public $_dbop;	public $_id;	public $_template_name;	public $_template_title;	public $_template_content;	public $_on_time;	public $_cache_config = array ('is_cache' => 0, 'time' => 0 );	public $_replace=0;	public $_where;	function  keke_witkey_task_template_class(){		$this->_db = new db_factory ( );		$this->_dbop = $this->_db->create(DBTYPE);		$this->_tablename = TB_PRE."witkey_task_template";	}	public function getId(){		return $this->_id ;	}	public function getTemplate_name(){		return $this->_template_name ;	}	public function getTemplate_title(){		return $this->_template_title ;	}	public function getTemplate_content(){		return $this->_template_content ;	}	public function getOn_time(){		return $this->_on_time ;	}	public function getWhere(){		return $this->_where ;	}	public function getCache_config() {		return $this->_cache_config;	}	public function setId($value){		$this->_id = $value;	}	public function setTemplate_name($value){		$this->_template_name = $value;	}	public function setTemplate_title($value){		$this->_template_title = $value;	}	public function setTemplate_content($value){		$this->_template_content = $value;	}	public function setOn_time($value){		$this->_on_time = $value;	}	public function setWhere($value){		$this->_where = $value;	}	public function setCache_config($_cache_config) {		$this->_cache_config = $_cache_config;	}	public  function __set($property_name, $value) {		$this->$property_name = $value;	}	public function __get($property_name) {		if (isset ( $this->$property_name )) {			return $this->$property_name;		} else {			return null;		}	}	function create_keke_witkey_task_template(){		$data =  array();		if(!is_null($this->_id)){			$data['id']=$this->_id;		}		if(!is_null($this->_template_name)){			$data['template_name']=$this->_template_name;		}		if(!is_null($this->_template_title)){			$data['template_title']=$this->_template_title;		}		if(!is_null($this->_template_content)){			$data['template_content']=$this->_template_content;		}		if(!is_null($this->_on_time)){			$data['on_time']=$this->_on_time;		}		return $this->_id = $this->_db->inserttable($this->_tablename,$data,1,$this->_replace);	}	function edit_keke_witkey_task_template(){		$data =  array();		if(!is_null($this->_id)){			$data['id']=$this->_id;		}		if(!is_null($this->_template_name)){			$data['template_name']=$this->_template_name;		}		if(!is_null($this->_template_title)){			$data['template_title']=$this->_template_title;		}		if(!is_null($this->_template_content)){			$data['template_content']=$this->_template_content;		}		if(!is_null($this->_on_time)){			$data['on_time']=$this->_on_time;		}		if($this->_where){			return $this->_db->updatetable($this->_tablename,$data,$this->getWhere());		}		else{			$where = array('id' => $this->_id);			return $this->_db->updatetable($this->_tablename,$data,$where);		}	}	function query_keke_witkey_task_template($is_cache=0, $cache_time=0){		if($this->_where){			$sql = "select * from $this->_tablename where ".$this->_where;		}		else{			$sql = "select * from $this->_tablename";		}		if ($is_cache) {			$this->_cache_config ['is_cache'] = $is_cache;		}		if ($cache_time) {			$this->_cache_config ['time'] = $cache_time;		}		if ($this->_cache_config ['is_cache']) {			if (CACHE_TYPE) {				$keke_cache = new keke_cache_class ( CACHE_TYPE );				$id = $this->_tablename . ($this->_where?"_" .substr(md5 ( $this->_where ),0,6):'');				$data = $keke_cache->get ( $id );				if ($data) {					return $data;				} else {					$res = $this->_dbop->query ( $sql );					$keke_cache->set ( $id, $res,$this->_cache_config['time'] );					$this->_where = "";					return $res;				}			}		}else{			$this->_where = "";			return  $this->_dbop->query ( $sql );		}	}	function count_keke_witkey_task_template(){		if($this->_where){			$sql = "select count(*) as count from $this->_tablename where ".$this->_where;		}		else{			$sql = "select count(*) as count from $this->_tablename";		}		$this->_where = "";		return $this->_dbop->getCount($sql);	}	function del_keke_witkey_task_template(){		if($this->_where){			$sql = "delete from $this->_tablename where ".$this->_where;		}		else{			$sql = "delete from $this->_tablename where id = $this->_id ";		}		$this->_where = "";		return $this->_dbop->execute($sql);	}}?>